INSERT INTO `wk_archive` VALUES ('1', '1', 'f', '1', '1', '198', '20', '高朋前9月净亏4645万美元 营收仅为214万美元', '', '', '不详', '网络', '', '1359179109', '1359179109', '1', '', '\r\n	新浪科技讯 \r\n10月25日上午消息，高朋网在华业绩和所获投资首次曝光，据美国团购网站Groupon提交的招股说明书文件显示，高朋网在华全资母公司E-\r\nCommerce King截至2011年9月30日前9个月净亏损4645万美元，同期营收仅为214万美元，总销售额为1844万美元。\r\n\r\n\r\n	高朋网在中国大陆经营一家团购网站，通过多家网站以及社交与交互媒体面向个人消费者和企业提供产品与服务折扣。高朋网从2011年3月开始在北京和上海提供团购，随后业务扩展至中国其他...', '0');
