INSERT INTO `wk_archive` VALUES ('41', '5', '', '1', '1', '167', '20', '百度MP3等违法音乐网站将被立案查处', '', '', '不详', '网络', '', '1359181338', '1359181338', '1', '', '继今年1月清理违规网络音乐产品后，文化部日前再度下文，要求查处违法网络音乐网站及低俗网络游戏宣传推广活动。其中，百度MP3位居涉嫌违法网络音乐网站榜首，而迅雷网络技术有限公司等公司则涉嫌违法推广和宣传网络游戏。\r\n\r\n　据文化部相关负责人介绍，今年1月7日，文化部公布首批100首未经内容审查或备案的违规网络音乐产品，要求各大网站于2月28日之前开展自查自纠，进\r\n行清理整改。3月中旬，文化部决定对逾期未完成清理整改、仍在擅自提供首批100首违规网络音乐产品服务的54家网络音乐...', '0');
